/* 
React component that renders an image
if the image fails to load, a fallback placeholder UI is displayed
*/
import React, { useState } from 'react'

/*renders a Base64-encoded SVG when the real image fails to load */ 
const ERROR_IMG_SRC =
  'data:image/svg+xml;base64,'

/* 
function accepts all normal <img> attributes (src, alt, className etc)
uses boolean state didError to track whether the image load failed 
when the <img> element encounters a loading error, the onError from the rendered <img> calls handleError which sets didError to true
*/ 
export function ImageWithFallback(props: React.ImgHTMLAttributes<HTMLImageElement>) {
  const [didError, setDidError] = useState(false)

  const handleError = () => {
    setDidError(true)
  }

  const { src, alt, style, className, ...rest } = props

  return didError ? (
    <div
      className={`inline-block bg-gray-100 text-center align-middle ${className ?? ''}`}
      style={style}
    >
      <div className="flex items-center justify-center w-full h-full">
        <img src={ERROR_IMG_SRC} alt="Error loading image" {...rest} data-original-url={src} />
      </div>
    </div>
  ) : (
    <img src={src} alt={alt} className={className} style={style} {...rest} onError={handleError} />
  )
}
